import React, { useState, useEffect } from "react";
import StartPage from "./components/StartPage";
import QuizPage from "./components/QuizPage";
import ReportPage from "./components/ReportPage";
import fetchQuestions from "./api/fetchQuestions";

const App = () => {
  const [email, setEmail] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [isQuizFinished, setIsQuizFinished] = useState(false);

  useEffect(() => {
    if (email) {
      fetchQuestions().then((data) => setQuestions(data));
    }
  }, [email]);

  const handleQuizFinish = (submittedAnswers) => {
    setAnswers(submittedAnswers);
    setIsQuizFinished(true);
  };

  return (
    <div>
    <Header />
      {!email && <StartPage onStart={setEmail} />}
      {email && !isQuizFinished && <QuizPage questions={questions} onSubmit={handleQuizFinish} />}
      {isQuizFinished && <ReportPage questions={questions} answers={answers} />}
    </div>
  );
};

export default App;
